AS
SELECT s.staff_id AS ID, CONCAT(s.first_name, _utf8'' '', s.last_name) AS name, a.address AS address, a.postal_code AS `zip code`, a.phone AS phone,
	city.city AS city, country.country AS country, s.store_id AS SID
